declare
  v_user_id uuid;
  v_role text;
  v_org_id uuid;
  v_venue json;
  v_shows json;
  v_contacts json;
begin
  -- Get current user
  v_user_id := auth.uid();
  if v_user_id is null then
    raise exception 'Not authenticated';
  end if;

  -- Get the venue's org_id
  select org_id into v_org_id
  from venues
  where id = p_venue_id;

  if v_org_id is null then
    raise exception 'Venue not found';
  end if;

  -- Check membership
  select role into v_role
  from org_members
  where org_id = v_org_id
    and user_id = v_user_id
    and status = 'active';

  if v_role is null then
    raise exception 'Access denied';
  end if;

  -- Get venue details
  select json_build_object(
    'id', v.id,
    'org_id', v.org_id,
    'name', v.name,
    'address', v.address,
    'city', v.city,
    'country', v.country,
    'capacity', v.capacity,
    'notes', v.notes,
    'created_at', v.created_at
  ) into v_venue
  from venues v
  where v.id = p_venue_id;

  -- Get shows at this venue
  select coalesce(json_agg(
    json_build_object(
      'id', s.id,
      'title', s.title,
      'date', s.date,
      'status', s.status,
      'doors_at', s.doors_at,
      'set_time', s.set_time
    ) order by s.date desc
  ), '[]'::json) into v_shows
  from shows s
  where s.venue_id = p_venue_id;

  -- Get contacts linked to this venue
  select coalesce(json_agg(
    json_build_object(
      'id', c.id,
      'name', c.name,
      'email', c.email,
      'phone', c.phone,
      'company', c.company,
      'contact_type', c.contact_type,
      'is_primary', vc.is_primary,
      'role_label', vc.role_label
    )
  ), '[]'::json) into v_contacts
  from venue_contacts vc
  join contacts c on c.id = vc.contact_id
  where vc.venue_id = p_venue_id;

  return json_build_object(
    'venue', v_venue,
    'shows', v_shows,
    'contacts', v_contacts
  );
end;

